import React from "react"
import axios from "axios"

export default function Meme(){

    const [meme, setMeme] = React.useState({
        topText: "",
        bottomText: "",
        randomImage: "https:\/\/i.imgflip.com\/1ur9b0.jpg"
    })

    function getNewImage(){
        console.log("got image")


    }

    function handleChange(event){
        const {name, value} = event.target

        setMeme(prevMeme => ({
            ...prevMeme,
            [name]: value
        }))
    }

    return(
        <section>

            <div className="form">

                <input 
                    type="text"
                    name="topText"
                    className="form--input"
                    placeholder="Top Text"
                    value={meme.topText}
                    onChange={handleChange}
                />

                <input 
                    type="text"
                    name="bottomText"
                    className="form--input"
                    placeholder="Bottom Text"
                    value={meme.bottomText}
                    onChange={handleChange}
                />

                <button 
                className="form--button"
                onClick={getNewImage}
                >
                    Get New Image
                </button>

                <button className="form--button">Add to List</button>

            </div>

            <div className="preview--meme">
                <img className="preview--meme--image" src={meme.randomImage}/>
                <h2 className="preview--meme--text top">{meme.topText}</h2>
                <h2 className="preview--meme--text bottom">{meme.bottomText}</h2>
            </div>

        </section>
    )
}
